import os

from internetofmoney.tests.test_base import BaseTestCase
from internetofmoney.utils.ingcrypto import INGCryptor


class TestINGCrypto(BaseTestCase):
    """
    This class contains tests for the ING symmetric crypto.
    """

    def test_encrypt_decrypt(self):
        """
        Test encryption/decryption of some text.
        """
        cryptor = INGCryptor("a" * 16)
        encrypted = cryptor.encrypt("test")
        self.assertTrue(encrypted)
        self.assertEqual(cryptor.decrypt(encrypted), "test")
