import os

from internetofmoney.tests.test_base import BaseTestCase
from internetofmoney.utils.ingsymmetrickey import INGSymmetricKey


class TestINGSymmetricKey(BaseTestCase):
    """
    This class contains tests for the ING symmetric key
    """

    def test_get_symmetric_key(self):
        """
        Test the generation of a symmetric key
        """
        sym_key = INGSymmetricKey(self.temp_dir)
        key = sym_key.get_symmetric_key()
        self.assertEqual(len(key), 32)
        self.assertEqual(key, sym_key.get_symmetric_key())
