class INGNumberPadder(object):

    @staticmethod
    def left_data_padding_to_length(number, pad_number):
        """
        Left-pad a specific string.
        """
        if pad_number > len(number):
            count = pad_number - len(str(number))

            # Pad with the character value 0, not with character 0!
            return str(number).rjust(count, chr(0)).encode('hex')
        else:
            return number.encode('hex')
